import React from "react";

export default function Scoreboard({ scoreValue }) {
  const currentUser = localStorage.getItem("currentUser");
  let num = 1;

  // Validation of User
  const validUser = () => {
    if (currentUser !== "undefined" && currentUser !== null) {
      return true;
    }
  };
  console.log("Validation", validUser);
  // Check User
  const newUser = () => {
    if (validUser) {
      return <td>{currentUser}</td>;
    } else {
      return <td>Guest</td>;
    }
  };
  // High Score
  const highScore = scoreValue.slice(0, 3).map((newScore) => (
    <tr>
      <th>{num++}</th>
      {newUser}
      <td>{newScore}</td>
    </tr>
  ));

  return (
    <React.Fragment>
      <table className="table">
        <thead>
          <tr className="table-info">
            <th>#</th>
            <th>User</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {/* {validUser && <td>{currentUser}</td>} */}
          {highScore}
        </tbody>
      </table>
    </React.Fragment>
  );
}
