import React from "react";

export default function Scoreboard({ scoreValue }) {
  const currentUser = localStorage.getItem("currentUser");
  let num = 1;
  // ID Increment
  // let scoreId = (x) => {
  //   x + 1;
  // };

  // High Score
  const highScore = scoreValue.slice(0, 3).map((newScore) => (
    <tr>
      <th>{num + 1}</th>
      <td>User</td>
      <td>{newScore}</td>
    </tr>
  ));

  // Validation of User
  const validUser = () => {
    if (currentUser !== "undefined" && currentUser !== null) {
      return true;
    }
  };

  return (
    <React.Fragment>
      <table className="table">
        <thead>
          <tr className="table-info">
            <th>#</th>
            <th>User</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {/* <tr> */}
          {/* <th>{x}</th> */}
          {/* {validUser && <td>{currentUser}</td>} */}
          {highScore}

          {/* <td>{scoreValue}</td> */}
          {/* </tr> */}
        </tbody>
      </table>
    </React.Fragment>
  );
}
